 */
export class LineSpiral<T> extends SpiralBase<T> {
    private hist_data: d3Array.Bin<T, number>[];
    private hist_fn: d3Array.HistogramGenerator<T, number>;
    private n_points = 10000;
    private hist_x0 = d3.scaleLinear().range([0, 1]);
